mapper.select:
SELECT member_passwd 
	FROM member
	WHERE member_id=#{sId}

mapper.update:
UPDATE member
	SET
		member_passwd = #{secureNewPassword}
	WHERE 
		member_id = #{member.member_id}

mapper.delete:
DELETE FROM wishlist
	WHERE 
		member_idx = #{member_idx}
	AND 
		product_idx = #{product_idx}

mapper.select:
SELECT COUNT(*) 
	FROM address
	WHERE member_idx=#{member_idx}

SELECT o.order_buy_idx, o.product_idx, o.member_idx, o.order_buy_gb, date_format(o.order_buy_date, '%y-%m-%d') order_buy_date, i.image_real_file1, p.product_name, p.product_buy_price FROM order_buy o LEFT JOIN image i ON o.product_idx = i.product_idx LEFT JOIN product p ON o.product_idx = p.product_idx
WHERE o.member_idx = #{member_idx}
AND date_format(str_to_date(o.order_buy_date, '%Y-%m-%d'), '%y-%m-%d') >= date_format(str_to_date(#{date1}, '%Y-%m-%d'), '%y-%m-%d') AND date_format(str_to_date(o.order_buy_date, '%Y-%m-%d'), '%y-%m-%d') <= date_format(str_to_date(#{date2}, '%Y-%m-%d'), '%y-%m-%d')
ORDER BY date_format(str_to_date(o.order_buy_date, '%Y-%m-%d'), '%y-%m-%d') DESC LIMIT #{startRow}, #{listLimit}
